mapper.insert:
INSERT INTO med_info(
	MED_ID
	, USER_ID
	, MED_NAME
	, START_DATE
	, END_DATE 
	, MED_STATUS
) VALUES(
	#{medId}
	, #{userId}
	, #{medName}
	, #{startDate}
	, #{endDate}
	, 'Y'
)

mapper.update:
UPDATE 
	med_info 
SET 
	MED_NAME = #{medName}
	, START_DATE = #{startDate}
	, END_DATE = #{endDate}
WHERE
	MED_ID = #{medId}
	AND USER_ID = #{userId}

UPDATE 
	med_info 
SET 
	med_status = #{medStatus}
WHERE
	MED_ID = #{medId}
	AND USER_ID = #{userId}
