mapper.insert:
INSERT INTO alarm_set(
	ALARM_ID
	, USER_ID
	, ALARM_TYPE
	, ALARM_TIME
) VALUES(
	#{alarmId}
	, #{userId}
	, #{alarmType}
	, #{alarmTime}
)

mapper.update:
UPDATE 
	alarm_set 
SET 
	ALARM_TIME = #{alarmTime}
	, ALARM_INTERVAL = #{alarmInterval}
	, RPT_INTERVAL = #{rptInterval}
	, ALARM_USE_AT = #{alarmUseAt}
WHERE
	ALARM_ID = #{alarmId}

mapper.insert:
INSERT INTO med_info(
	MED_ID
	, USER_ID
	, MED_NAME
	, START_DATE
	, END_DATE 
	, DURATION 
	, MED_STATUS
) VALUES(
	#{medId}
	, #{userId}
	, #{medName}
	, #{startDate}
	, #{endDate}
	, #{duration} 
	, 'Y'
)

mapper.update:
UPDATE 
	med_info 
SET 
	MED_NAME = #{medName}
	, START_DATE = #{startDate}
	, END_DATE = #{endDate}
	, DURATION = #{duration}
WHERE
	MED_ID = #{medId}

mapper.insert:
INSERT INTO alarm_reg(
	MED_ID
	, ALARM_ID
) VALUES(
	#{medId}
	, #{alarmId}
)
