mapper.select:
SELECT A.MED_ID , A.MED_NAME , A.START_DATE , A.END_DATE , A.MED_STATUS , B.ALARM_ID , C.SCHE_CHK FROM med_info A JOIN schedule B ON A.MED_ID = B.MED_ID LEFT JOIN ( SELECT MED_ID , ALARM_ID , SCHE_CHK FROM sche_log WHERE LOG_STATUS = 'Y' AND USER_ID = #{userId} AND SCHE_DATE = #{selectedDay} ) AS C ON B.MED_ID = C.MED_ID AND B.ALARM_ID = C.ALARM_ID WHERE A.USER_ID = #{userId}
AND (A.START_DATE <= #{selectedDay} AND A.END_DATE >= #{selectedDay})
ORDER BY A.MED_ID

mapper.insert:
INSERT INTO schedule(
	MED_ID
	, ALARM_ID
) VALUES(
	#{medId}
	, #{alarmId}
)

mapper.delete:
DELETE FROM schedule
WHERE MED_ID = #{medId}

	AND ALARM_ID NOT IN (
	    SELECT ALARM_ID
	    FROM alarm_set
	    WHERE ALARM_TYPE IN (

	            #{resultAlarmType}

	    )
	)

mapper.select:
SELECT 
	MED_ID
	, ALARM_ID
	, SCHE_DATE
FROM sche_log
WHERE
	MED_ID = #{medId}
	AND ALARM_ID = #{alarmId}
	AND USER_ID = #{userId}


			AND SCHE_DATE = #{scheDate}


			AND SCHE_DATE = CURDATE()

mapper.insert:
INSERT INTO sche_log (
	MED_ID
	, ALARM_ID 
	, USER_ID
	, SCHE_DATE 
	, SCHE_CHK 
	, LOG_STATUS 
) VALUES(
	#{medId}
	, #{alarmId}
	, #{userId}


			, #{scheDate}


			, CURDATE()


	, 'Y'
	, 'Y'
)

mapper.update:
UPDATE sche_log SET


			LOG_STATUS = #{logStatus}


			SCHE_CHK = #{scheChk}


WHERE 
	MED_ID = #{medId}
	AND ALARM_ID = #{alarmId}
	AND USER_ID = #{userId}


			AND SCHE_DATE = #{scheDate}


			AND SCHE_DATE = CURDATE()
