mapper.insert:
INSERT INTO alarm_set(
	ALARM_ID
	, USER_ID
	, ALARM_TYPE
	, ALARM_TIME
	, ALARM_USE_AT
) VALUES(
	#{alarmId}
	, #{userId}
	, #{alarmType}
	, #{alarmTime}
	, 'Y'
)

mapper.update:
UPDATE 
	alarm_set 
SET 
	ALARM_TIME = #{alarmTime}
WHERE
	ALARM_ID = #{alarmId}
	AND USER_ID = #{userId}

mapper.insert:
INSERT INTO schedule(
	MED_ID
	, ALARM_ID
) VALUES(
	#{medId}
	, #{alarmId}
)

mapper.delete:
DELETE FROM schedule
WHERE MED_ID = #{medId}

	AND ALARM_ID NOT IN (
	    SELECT ALARM_ID
	    FROM alarm_set
	    WHERE ALARM_TYPE IN (

	            #{resultAlarmType}

	    )
	)
