mapper.insert:
INSERT INTO users (
	user_id
	, password
	, email
	, user_carer_at)
VALUES (
	#{userId}
	, #{password}
	, #{email}
	, #{userCarerAt})

mapper.select:
SELECT COUNT(*) 
FROM users 
WHERE user_id = #{userId}

SELECT user_name
FROM users
WHERE user_id = #{userId}

mapper.update:
UPDATE users 
SET
	user_name = #{userName}
	, user_phone = #{userPhone}
	, user_gender = #{userGender}
	, user_height = #{userHeight}
	, user_weight = #{userWeight}
	, user_birth = #{userBirth}
	, email = #{userEmail}
WHERE user_id = #{userId}

mapper.select:
	SELECT user_carer_at
