mapper.select:
SELECT COUNT(*) 
FROM users 
WHERE user_id = #{userId}

SELECT user_id
FROM users
WHERE email = #{email}

mapper.update:
UPDATE users
SET password = #{password}  		
WHERE user_id = #{userId} AND email = #{email}

mapper.select:
SELECT user_id, email
FROM users
WHERE user_id = #{userId} AND email = #{email}
